class FileColumn extends BaseFileColumn
{
    public function getColumnasRegistros(){
		$q = Doctrine::getTable("FileColumn")
                                 ->createQuery("c")
                                 ->where("c.ca_idregistro = ?", $this->getCaIdcolumna() )
                                 ->addOrderBy("c.ca_idcolumna");
                                 
                return $q->execute();
	}
}
