<?php

/**
 * FormularioTable
 * 
 * This class has been auto-generated by the Doctrine ORM Framework
 */
class FormularioTable extends Doctrine_Table {

    /**
     * Returns an instance of this class.
     *
     * @return object FormularioTable
     */
    public static function getInstance() {
        return Doctrine_Core::getTable('Formulario');
    }

    /**
     * Devuelve un formulario dado un id.. este metodo sobra ya que se utiliza el metodo find()
     * @param type $id_articulo
     * @return type
     */
    public function getFormulario($id_formulario) {
        $q = Doctrine_Query::create()
                ->from('formulario')
                ->where('ca_id = ?', $id_formulario)
                ->limit(1);
        return $q->fetchOne();
    }

    public static function contarFormularios(Doctrine_Query $q = null) {
        $q = Doctrine_Query::create()
                ->from('formulario j');
        return $q->count();
    }

}