class ResultadoEncuesta extends BaseResultadoEncuesta {

    public function getPromedio($id_resultado) {
        $q = Doctrine_Query::create()
                ->from('ResultadoEncuesta')
                ->where('ca_idcontrolencuesta = ?', $id_resultado);
                //->andWhere('ca_resultado = ?', '1' or '2' or '3' or '4' or '5')
                //->orderBy('ca_orden ASC');
        return $q->fetchOne();
    }

