    public function getReadonly( $nivel ) {

        
        $user = sfContext::getInstance()->getUser();
        $q = Doctrine_Query::create()->from("HdeskTicket h");
        $q->where("h.ca_idticket = ?", $this->getCaIdticket());

        switch ($nivel) {
            case 0:
                $q->leftJoin("h.HdeskTicketUser hu  ");
                $q->addWhere("(h.ca_login = ? OR hu.ca_login = ?)", array($user->getUserid(), $user->getUserid()));

                break;
            case 1:
                $q->leftJoin("h.HdeskUserGroup ug ");
                $q->leftJoin("h.HdeskTicketUser hu  ");
                $q->addWhere("(h.ca_login = ? OR ug.ca_login = ? OR hu.ca_login = ? )", array($user->getUserid(), $user->getUserid(), $user->getUserid()));
                break;
            case 2:
                $q->leftJoin("h.HdeskGroup g ");
                $q->leftJoin("h.HdeskTicketUser hu  ");
                $q->addWhere("(h.ca_login = ? OR g.ca_iddepartament = ? OR hu.ca_login = ?)", array($user->getUserid(), $user->getIddepartamento(), $user->getUserid()));
                break;
        }

        $ticket = $q->fetchOne();
        if( $ticket ){
            return false;
        }else{
            return true;
        }
    }
    
    public function getActualizarPorcentaje() {
        $estimations = $this->getHdeskEstimations();             
        if(count($estimations) > 0){
            $j=0;
            foreach($estimations as $estimation){
                if($estimation->getCaIdresponse())
                    $j++;
            }
            if($j>0){
                $this->setCaPercentage(number_format($j*100/count($estimations),0));
                $this->save();
            }
        }
    }
    
    public function getHdeskEstimations($modo=null) {

        $q =  Doctrine::getTable("HdeskEstimations")->createQuery("e")
                ->where("e.ca_idticket = ?", $this->getCaIdticket())
                ->addOrderBy("e.ca_estimated, ca_fchcreado ASC");
        
        if($modo)
            $q->addWhere("e.ca_idresponse IS NULL");
        
        return $q->execute();
    }
    
    public function setDocumento($tipo, $documento){
        $q = Doctrine::getTable('HdeskAuditDocuments')
                ->createQuery('a')
                ->where('a.ca_idticket = ? and a.ca_tipo_doc = ? and a.ca_numero_doc = ?', array($this->getCaIdticket(),$tipo, $documento))
                ->execute();
        
        if(count($q)< 1){
            switch($tipo){
                case "Reporte de Negocios":
                    $observaciones = '<a href="/colsys_php/reporteneg.php?id='.$documento.'" target="_blank">Ver Reporte</a>';
                    break;
                case "INO":
                case "Contenedores":
                    $observaciones = '<a href="/inoF2/indexExt5/idmaster/'.$documento.'" target="_blank">Ver Referencia</a>';
                    $master = Doctrine::getTable("InoMaster")->find($documento);
                    $documento = $master->getCaReferencia();
                    break;
                default:
                    $observaciones = "";
                    break;
            }
            $doc =  new HdeskAuditDocuments();
            $doc->setCaIdticket($this->getCaIdticket());
            $doc->setCaTipoDoc($tipo);
            $doc->setCaNumeroDoc($documento);
            $doc->setCaObservaciones($observaciones);
            $doc->save();
        }
    }
    
    public function getEmail(){
    
        $ticket = Doctrine::getTable("HdeskTicket")->find($this->getCaIdticket());
        
        $q = Doctrine::getTable("Email")
                ->createQuery("e")
                ->select("MAX(ca_idemail) as maxEmail")
                ->where("e.ca_idcaso = ?", $this->getCaIdticket())
                ->fetchOne();                               
        
        return $q->maxEmail;        
    }
