class IdsSucursal extends BaseIdsSucursal
{
    public function getContactos(){
       
        $contactos =  Doctrine::getTable("IdsContacto")
                  ->createQuery("c")
                   ->where("c.ca_idsucursal = ?", $this->getCaIdsucursal())
                  ->addWhere("c.ca_fcheliminado IS NULL")
                  ->addOrderBy("c.ca_sugerido ASC")
                  ->addOrderBy("c.ca_nombres ASC")
                  ->execute();
                  //print_r( $contactos );
        return $contactos;
        
    }

}