                // $comprobante = Doctrine::getTable("InoComprobante")->findOneBy("ca_idcomprobante", $tr["ca_indice1"]);
                $sql = "select ca_idcomprobante, ca_consecutivo from ino.tb_comprobantes where ca_idcomprobante = '" . $tr["ca_indice1"] . "'";
