class Convivencia extends BaseConvivencia
{
    const FOLDER = "Formularios";
    
    public function getDirectorio() {        
        return $directory = sfConfig::get('app_digitalFile_root') . DIRECTORY_SEPARATOR . $this->getDirectorioBase();
    }

