class PricFleteBs extends BasePricFleteBs
{
    /*
	* Retorna el estilo que se debe aplicar a las filas de acuerdo al
	* estado que se encuentre
	* Author: Andres Botero
	*/
	public function getEstilo(){
		switch( $this->getCaEstado() ){
			case 1:
				return "yellow";
				break;
			case 2:
				return "pink";
				break;
                        case 3:
				return "green";
				break;
			default:
				return "";
				break;
		}
	}

	/*
	* Coloca el estilo que se debe aplicar a las filas de acuerdoa al estado que se encuentre
	* Author: Andres Botero
	*/
	public function setEstilo( $estilo ){
		switch( $estilo ){
			case "yellow":
				$this->setCaEstado(1);
				break;
			case "pink":
				$this->setCaEstado(2);
				break;
                        case "green":
				$this->setCaEstado(3);
				break;
			default:
				$this->setCaEstado(null);
				break;
		}
	}
}