class TrackingEtapa extends BaseTrackingEtapa
{
    public function getIntroAsunto(){
        if($this->getCaPreasunto()!="")
        {
            $asunto=$this->getCaPreasunto()." ";
        }
        else
        {
            if( $this->getCaDepartamento()=="OTM/DTA" ){
                $asunto = "Status OTM";
            }else{
                $asunto = "Status";
            }
        }		
		return $asunto;
	}
}

